import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
///////////////////////////////////////////////////////////////////////////////////////////////////
import Emptycart from "../isLoading/EmptyCart";
import {
  Box,
  Card,
  CardActions,
  Link,
  CardContent,
  CardMedia,
  Button,
  Typography,
} from "@mui/material";

const Cart = ({ allData }) => {
  const dataaa = useSelector((state) => state);

  const data1 = dataaa.user.user.payload;

  console.log(allData); //props.allData._id===cart._id_Product return e

  const dataProduct = [];

  //Check id user
  if (data1 !== undefined) {
    const cart = data1.dataUser.cart;

    const arr = [];
    for (let i = 0; i < cart.length; i++) {
      let value_ID = cart[i]._id_Product;
      allData.filter((e) => {
        if (e._id === value_ID) {
          // arr.push(e)
          let { foodName, images, price } = e;
          let obj = {
            foodName: foodName,
            images: images,
            price: price,
            quality: cart[i].quality,
          };
          arr.push(obj);
        }
      });
    }
    const titleTopTable = ["Product", "Quality", "Total"];

    return (
      <Box className="cardForm">
        <Card className="Form">
          <CardContent>
            <Box className="topTable">
              {titleTopTable.map((e) => (
                <Typography
                  className="element"
                  gutterBottom
                  variant="h5"
                  component="div" 
                >
                  {e}
                </Typography>
              ))}
            </Box>
            <Box>
              {arr.map((e) => (////Component -->map we have private state
                <Box>
                  <Typography
                    className=""
                    gutterBottom
                    variant="small"
                    component="div"
                  >
                    {e.foodName}
                  </Typography>
                  <Typography
                    className=""
                    gutterBottom
                    variant="small"
                    component="div"
                  >
                    {e.price}
                  </Typography>
                  <Typography
                    className=""
                    gutterBottom
                    variant="small"
                    component="div"
                  >
                    {e.price * e.quality}
                  </Typography>
                </Box>
              ))}
            </Box>
          </CardContent>
        </Card>
      </Box>
    );
  }

  return (
    <div>
      <Emptycart />
    </div>
  );
};

export default Cart;
