import React, { useEffect, useState } from "react";
import {
  Box,
  Card,
  CardActions,
  CardContent,
  CardMedia,
  Button,
  Typography,
  Link,
  Modal,
} from "@mui/material";
import { Route, Routes, useParams } from "react-router-dom";
import CardComponentShop from "../smallComponent/CardComponentShop";
import Checkingform from "../smallComponent/CheckingForm";

const Shop = ({ allData, isReady }) => {
  if (!isReady) {
    return <p>Loading.............</p>;
  }

  return (
    <Box>
      <Typography
        gutterBottom
        variant="h6"
        component="div"
        className=""
        style={{
          paddingLeft: "3vw",
          paddingTop: "1vw",
          outline: "none",
          color: "black",
        }}
      >
        >>
        <Link href="/shop" underline="none">
          Shop
        </Link>
      </Typography>
      <Box className="AllProductShop">
        {allData.map((e, index) => (
          <CardComponentShop data={e} key={index} />
        ))}
        <Checkingform />
      </Box>
    </Box>
  );
};

export default Shop;
