import { createSlice, createAsyncThunk } from "@reduxjs/toolkit"
import axios from "axios"

const url = "http://localhost:5000/api/private/read"

export const getDataPizza = createAsyncThunk(
    "read/pizza",
    async () => {
        return await axios.get(`${url}/pizza`) .then(
            res=>res.data
        )
    }
)

export const getDataSushi = createAsyncThunk(
    "read/sushi",
    async () => {
        return await axios.get(`${url}/sushi`).then(
            res=>res.data
        )
    }
)

const dataPizzaSlice = createSlice({
    name: "pizza",
    initialState: {},
    extraReducers: {
        [getDataPizza.pending]: (state, action) => {
            state.status = "loading";
        },
        [getDataPizza.fulfilled]: (state, action) => {
          state.status = "success";
          state.pizza = action.payload;
        },
        [getDataPizza.rejected]: (state, action) => {
            state.status = "failed";
        },
    }
})

//Reducers

const dataPizzaReducers = dataPizzaSlice.reducer

export default  dataPizzaReducers ;
