import React, { useState,useEffect } from 'react';
import { Box, Card, CardActions, Link, CardContent, CardMedia, Button, Typography } from '@mui/material';
import AOS from "aos"
import "aos/dist/aos.css"

const CardComponent = ({ data }) => {

    useEffect(() => {
        AOS.init({
            offset: 200,
            // duration: 10,
            // easing: 'ease-in-sine',
            // delay: 10,
          });
    }, [])

  //dropDown button
  const [toggleClick, setToggleClick] = useState(false)

  ///////////Function handle event
   const handleToggleHover = ()=> {
    setToggleClick(true)
  }
  const handleToggleLeave = ()=> {
      setToggleClick(false)
  }
  
  const classNameChangeColor = toggleClick ? "hoverImageCard" : "dispayNone"

  return (
        <Card className="childElementCard" data-aos="zoom-in-down">
            <CardActions onMouseEnter={handleToggleHover}
                        onMouseLeave={handleToggleLeave}>
                {/* <Link href="#"variant="body1"  underline="none"> */}
                <Box className={classNameChangeColor}>
                  <Button
                      className="hoverButton" underline="none">
                      <Link href={`/product/${data._id}`}>
                            Order now
                      </Link>
                        </Button>
                        <CardContent>
                              <Typography
                                  gutterBottom
                                  variant="h6"
                                  component="div"
                                  className="textNameFood"
                              >
                                    {data.foodName}
                              </Typography>
                              <Typography
                                  gutterBottom
                                  variant="h6"
                                  component="div"
                                  className="textNameFood"
                              >
                                    {data.price} $
                              </Typography>
                        </CardContent>
                        {console.log(data)}
                    </Box>
                    <CardMedia
                        component="img"
                        alt="This is product"
                        className="imageCard"
                        image={data.images[0].imageUrl }
                    />
                {/* </Link> */}
            </CardActions>
        </Card>
  )
};

export default CardComponent
