import { PostDataLogin } from "../features/dataUserCreateAsyncthunk";
import { createSlice } from "@reduxjs/toolkit";
const PostDataLoginSlice = createSlice({
  name: "login",
  initialState: {},
  extraReducers: {
    [PostDataLogin.pending]: (state, action) => {
      state.status = "loading";
    },
    [PostDataLogin.fulfilled]: (state, action) => {
      state.status = "success";
      state.login = action.payload;
    },
    [PostDataLogin.rejected]: (state, action) => {
      state.status = "failed";
    },
  },
});

const PostDataLoginReducer = PostDataLoginSlice.reducer;
export { PostDataLoginReducer };
