import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  user: {},
  checkLogin: false,
  product: {},
};

export const getDataUserSlice = createSlice({
  name: "getDataUser",
  initialState,
  reducers: {
    getDataUser: (state, p) => {
      state.user = p;
    },
    checkLoginTrue: (state) => {
      state.checkLogin = true;
    },
    checkLoginFalse: (state) => {
      state.checkLogin = false;
    },
    getDataProduct: (state, p) => {
      state.product = p;
    },
  },
});

export const { getDataUser, checkLoginTrue, checkLoginFalse, getDataProduct } =
  getDataUserSlice.actions;

export default getDataUserSlice.reducer;
