import React, { useState, useEffect } from 'react';
import {
    useParams,
    Link
} from "react-router-dom";

const Shop = (props) => {
    // const id = useParams()
    // console.log(id)
    return (
      <div>
        {
                props.data.map((e, index) => (
                <div>
                        <p key={index}>
                    {e.title}
                    <br />
                    {e.descript}
                    <Link to={`/product/${e.title}`}></Link>
                </p> 
                </div>
               
            ))    
        }
      </div>
  )
};

export default Shop;
