import React, { useState } from "react";
import { Box, Typography, TextField, Button, Paper, Link } from "@mui/material";
import { useForm } from "react-hook-form";
import { useDispatch, useSelector } from "react-redux";
import axios from "axios";
import { RequestLogin } from "../../../../store/AxiosLibrary";

const Login = () => {
  //2--->get Error from server side
  const [errEmail, setErrEmail] = useState("");
  const [errPassword, setErrPassword] = useState("");
  //1--->Handle form (react-hook-form)

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();

  const onSubmit = (data) => {
    RequestLogin(data).then((res) => {
      if(res.data.status)
      {

      }
      else
      {
        setErrEmail(res.data.err_email[0])
        setErrPassword()
      }
    });
  };

  return (
    <Box className="LoginForm">
      <Box className="form">
        <Typography variant="h5" className="loginText">
          Login
        </Typography>
        <form onSubmit={handleSubmit(onSubmit)}>
          <Box className="coverTextField">
            <TextField
              id="standard-basic"
              label="Email"
              variant="outlined"
              className="inputLogin"
              defaultValue=""
              fullWidth
              {...register("email")}
              error="true"
              helperText="this is error"
            />
          </Box>
          <Box className="coverTextField">
            <TextField
              id="standard-basic"
              label="Password"
              variant="outlined"
              type="password"
              className="inputLogin"
              defaultValue=""
              fullWidth
              {...register("password")}
              // error="true"
              // helperText="this is error"
            />
          </Box>
          <Button variant="contained" className="buttonLogin" type="submit">
            Log In
          </Button>
          <br></br>
          <Typography variant="p" className="registerText">
            Do you have account ?
            <Link href="/signup" className="registerLink">
              Sign up
            </Link>
          </Typography>
        </form>
      </Box>
    </Box>
  );
};

export default Login;
