import { Navigation, Pagination, Scrollbar, A11y } from "swiper";

import { Swiper, SwiperSlide } from "swiper/react";

// Import Swiper styles
import "swiper/css";
import "swiper/css/navigation";
import "swiper/css/pagination";
import "swiper/css/scrollbar";

const Carousel = () => {
  const dataForCarousel = [
    "https://cdn.shopify.com/s/files/1/0740/5717/files/h1-slide-1.jpg?v=1602573149",
    "https://cdn.shopify.com/s/files/1/0740/5717/files/h1-slide-2.jpg?v=1602573148",
    "https://cdn.shopify.com/s/files/1/0740/5717/files/leo_foodo-h2-bn5.jpg?v=1602574972",
    "https://cdn.shopify.com/s/files/1/0740/5717/files/leo_foodo-h2-bn4.jpg?v=1602574972",
  ];

  return (
    <Swiper
      // install Swiper modules
      modules={[Navigation, Pagination, Scrollbar, A11y]}
      //   spaceBetween={50}
      slidesPerView={1}
      navigation
      //   pagination={{ clickable: true }}
      scrollbar={{ draggable: true }}
      onSwiper={(swiper) => console.log(swiper)}
      onSlideChange={() => console.log("slide change")}
    >
      {dataForCarousel.map((e) => (
        <SwiperSlide>
          <img
            src="https://helpx.adobe.com/content/dam/help/en/photoshop/using/convert-color-image-black-white/jcr_content/main-pars/before_and_after/image-before/Landscape-Color.jpg"
            alt=""
          />
        </SwiperSlide>
      ))}
    </Swiper>
  );
};

export default Carousel;
