////Library npm
import React, { useState, useEffect } from "react";
import {
  Box,
  Card,
  CardActions,
  Link,
  CardContent,
  CardMedia,
  Button,
  Typography,
} from "@mui/material";
// import AOS from "aos";
// import "aos/dist/aos.css";
import { useDispatch, useSelector } from "react-redux";

//////////////Actions

import {
  handleOpen,
  handlClose,
} from "../../../features/HandleClickToAppearCheckoutForm";

const CardComponentHome = ({ data }) => {
  const dispatch = useDispatch();

  const [toggleClick, setToggleClick] = useState(false);
  const handleToggleHover = () => setToggleClick(true);
  const handleToggleLeave = () => setToggleClick(false);

  const classNameChangeColor = toggleClick ? "hoverImageCard" : "dispayNone";

  return (
    <Card className="childElementCard">
      {/* <Link href="#"variant="body1"  underline="none"> */}
      <CardMedia
        component="img"
        alt="This is product"
        className="imageCard"
        image={data.images[0].imageUrl}
      />
      {/* </Link> */}
      <CardContent className="Go_foodName">
        <Typography
          gutterBottom
          variant="h6"
          component="div"
          className="textNameFood"
        >
          {data.foodName}
        </Typography>
        <Button className="Go_foodName">
              Go
          </Button>
      </CardContent>
    </Card>
  );
};

export default CardComponentHome;
