import { createSlice, createAsyncThunk } from "@reduxjs/toolkit"
import axios from "axios"

const url = "http://localhost:5000/api/private/read"

export const getDataPizza = createAsyncThunk(
    "pizza",
    async () => {
        return await axios.get(`${url}/pizza`) .then(
            res=>res.data.data
        )
    }
)

const dataSlice = createSlice({
    name: "pizza",
    initialState: {},
    extraReducers: {
        [getDataPizza.pending]: (state, action) => {
            state.status = "loading";
        },
        [getDataPizza.fulfilled]: (state, action) => {
          state.status = "success";
          state.data = action.payload;
        },
        [getDataPizza.rejected]: (state, action) => {
          state.status = "failed";
        },
    }
})

export default dataSlice.reducer;
