import React, { useEffect, useState } from "react";
import {
  Box,
  Card,
  CardActions,
  CardContent,
  CardMedia,
  Button,
  Typography,
  Modal,
  Link,
} from "@mui/material";

import AddIcon from "@mui/icons-material/Add";
import RemoveIcon from "@mui/icons-material/Remove";
//
import { useNavigate } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";

///API

import { handleClose } from "../../../features/HandleClickToAppearCheckoutForm";
import {addToCart} from "../../../store/AxiosLibrary"
import Cart from "../cart/Cart";

/////////////////////////////////////////////////////////////////////////////////////////

const Checkingform = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const [count, setCount] = useState(0);

  const open = useSelector((state) => state.handleClick.open);
  const dataCheckingForm = useSelector((state) => state);
  const checkLogin = useSelector((state) => state.user.checkLogin);

  const dataforForm = dataCheckingForm.handleClick.product.payload;

  //If user login push product in array product in mongodb alas
  //else redirect to /login

  const handleClick = async() => {////AddtoCart--->onClick if login -->put method to   server side
    if (!checkLogin) {
      navigate("/login");
    }
    else
    {
      await dispatch(handleClose())
      const sigleCart = {number:Cart,}
      await dispatch()//
    }
  };

  if (dataforForm == undefined) return <></>; ///////////Component loading

  const HandleClose = () => dispatch(handleClose());

  return (
    <Modal open={open} onClose={HandleClose} className="Form_Check_Infor">
      <Card className="cardForm">
        <Box className="elementCard">
          <CardMedia
            component="img"
            alt="Product is selected"
            className="imageCard"
            image={dataforForm.images[0].imageUrl}
          />
        </Box>
        <Box className="elementCard">
          <CardContent>
            <Typography
              gutterBottom
              variant="h6"
              component="div"
              className="test1"
            >
              {dataforForm.foodName}
            </Typography>
            <Typography
              gutterBottom
              variant="small"
              component="div"
              className="test1"
            >
              {dataforForm.description}
            </Typography>
            <Typography
              gutterBottom
              variant="small"
              component="div"
              className="test1"
            >
              {dataforForm.price} $
            </Typography>
          </CardContent>
          <Button onClick={() => setCount(count + 1)}>
            <AddIcon />
          </Button>
          {count}
          <Button
            onClick={() => {
              setCount(count > 1 ? count -1 : 0);
            }}
          >
            <RemoveIcon />
          </Button>
          <CardActions className="Button_CheckingForm">
            <Link href="/cart" underline="none">
              <Button>Go to cart </Button>
            </Link>
            <Button onClick={handleClick}>Add to cart</Button>
          </CardActions>
        </Box>
      </Card>
    </Modal>
  );
};

export default Checkingform;
