import React, { useEffect ,useState} from 'react';
import { Box, Card, CardActions, CardContent, CardMedia, Button, Typography,Modal } from '@mui/material';
import {
    Link,
    Route,
    Routes,
    useParams
} from "react-router-dom";
import CardComponent from '../smallComponent/CardComponent';

const Shop = ({ allData, isReady }) => {
    
    const [open, setOpen] = useState(false);
    const handleClose = () => setOpen(false);
    const handleOpen = () => setOpen(true);
    if (!isReady) 
    {
        return (
            <p>Loading.............</p>
        )
    }

        return (
            <Box className="AllProductShop">
                {
                    allData.map((e,index) => (
                        <CardComponent data={e}
                            handleOpen={ handleOpen()}
                        key={index} />
                    ))
                }
                <Box
                    className="Container_Form_Check_Infor"
                      >
                        <Modal
                          open={open}
                          onClose={handleClose}
                          className="Form_Check_Infor"
                        >
                        <Box>
                            <img src="https://www.google.com/url?sa=i&url=https%3A%2F%2Fdeveloper.mozilla.org%2Fen-US%2Fdocs%2FWeb%2FHTML%2FElement%2Fimg&psig=AOvVaw00kCOYjl00b9O-Fu60J9mt&ust=1644261732075000&source=images&cd=vfe&ved=0CAsQjRxqFwoTCOi7ibbm6_UCFQAAAAAdAAAAABAD" alt="" />
                        </Box>
                    </Modal>
                </Box>
            </Box>
           
        )
};

export default Shop;
