import React, { useEffect ,useState} from 'react';
import { Box, Card, CardActions, CardContent, CardMedia, Button, Typography } from '@mui/material';
import {
    Link,
    Route,
    Routes,
    useParams
} from "react-router-dom";
import CardComponent from '../smallComponent/CardComponent';

const Shop = ({ allData, isReady }) => {
    
    const [open, setOpen] = useState(props);
    const handleClose = () => setOpen(false);
    if (!isReady) 
    {
        return (
            <p>Loading.............</p>
        )
    }

        return (
            <Box className="AllProductShop">
                {
                    allData.map((e,index) => (
                        <CardComponent data={e}
                        key={index} />
                    ))
                }
                <Box
                    className="Container_Form_Check_Infor"
                      >
                        <Modal
                          open={open}
                          onClose={handleClose}
                          className="Form_Check_Infor"
                        >
                        <Box>
                            <p>Hello</p>
                        </Box>
                    </Modal>
                </Box>
            </Box>
           
        )
};

export default Shop;
