    Link,
    Route,
    Routes,
    useParams
} from "react-router-dom";
import CardComponent from '../smallComponent/CardComponent';
import {useSelector} from 'react-redux'

const Shop = ({ allData, isReady }) => {
    

    const handleClose = () => setOpen(false);
    const handleOpen = () => setOpen(true);
    const handleClick = useSelector(state=>state.handleClick)
    console.log(handleClick)

    if (!isReady) 
    {
        return (
            <p>Loading.............</p>
        )
    }


        return (
            <Box className="AllProductShop">
                {
                    allData.map((e,index) => (
                        <CardComponent data={e}
                        key={index} />
                    ))
                }
                <Box
                    className="Container_Form_Check_Infor"
                      >
                        <Modal
                          open={open}
                          onClose={handleClose}
                          className="Form_Check_Infor"
                        >
                        <Box>
         
                        </Box>
                    </Modal>
                </Box>
            </Box>
           
        )
};

export default Shop;
