import React, {  } from 'react';
import {
    useParams,
    Link
} from "react-router-dom";

const Shop = (props) => {
    return (
      <div>
        {
                props.data.map((e, index) => (
                <div>
                    <p key={index}>
                        {e.title}
                    </p> 
                    <p>
                        {e.description}
                    </p>
                    <Link to={`/product/${e.title}`}>View More</Link>
                </div>
               
            ))    
        }
      </div>
  )
};

export default Shop;
