import React, { useState, useEffect } from 'react';
import {
    useParams,
    Link
} from "react-router-dom";

const Shop = (props) => {
    // const id = useParams()
    // console.log(id)
    return (
      <div>
        {
            props.data.map((e,index) => (
                <p key={index}>
                    {e.title}
                    <br />
                    {e.name}
                    <Link to={`/product/${e.title}`}></Link>
                </p>
            ))    
        }
      </div>
  )
};

export default Shop;
