import React,{useEffect} from "react";
import {
  Box,
  Card,
  CardActions,
  Link,
  CardContent,
  CardMedia,
  Button,
  Typography,
} from "@mui/material";

import Cookies from "universal-cookie";

import { useSelector } from "react-redux";
import {Authorization} from "../../../store/AxiosLibrary"

import BusinessCenterIcon from "@mui/icons-material/BusinessCenter";

const Cart2 = () => {
  const cookies = new Cookies();
  const cart = useSelector((state) => state.user.user.payload);
  let quality = 0;
  const arrCart = cart.dataUser.cart;
  arrCart.map((e) => {
    quality += e.quality;
  });
  

  useEffect(async()=>{
    if (cookies.get("accessToken") != undefined) {
      const headers = { Authorization: "Bearer " + cookies.get("accessToken") };
      await Authorization({}, { headers: headers }).then(async (res) => {
        await dispatch(getDataUser(res.data));
        setCheckAcessToken(true);
        await dispatch(checkLoginTrue());
      });
    } else {
      setCheckAcessToken(false);
  }
  },[dispatch])
  

  return (
    <Box className="Cart_2">
      <Box>
        <Link href="/cart" underline="none" className="">
          <Box>
            <BusinessCenterIcon />
          </Box>
          <Box>
            <Typography
              className="qualityText"
              gutterBottom
              variant="p"
              component="div"
            >
              {quality}
            </Typography>
          </Box>
        </Link>
      </Box>
    </Box>
  );
};

export default Cart2;
