import { createSlice } from '@reduxjs/toolkit'

const initialState = {
    open: false
}

export const HandleClickSlice = createSlice({
    name:"HandleClick",
    initialState,
    reducers:
    {
        handleOpen:(state)=>
        {
            state.open = true
        },
        handleClose:(state)=>{
            state.open = false
        },
        getDataHandleClickPayload: (state,action)=>{
            state.value += action.payload
        }
    }
})

export const { handleOpen, handleClose, getDataHandleClickPayload } = HandleClickSlice.actions

export default HandleClickSlice.reducer
