import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    login:{}
}

export const LogInSlice = createSlice({
    name:"login",
    initialState,
    PostRequestLogin:(state,p)=>{
        state.login = p
    },
    getDataPostRequestLogin:(state,action)=>{
        state.value+=action.payload;
    }
})

export const {PostRequestLogin} = LogInSlice.actions

export default LogInSlice.reducer
