import React, { useState,useEffect } from 'react';
import { Box, Card, CardActions,Link, CardContent, CardMedia, Button, Typography } from '@mui/material';
const CardComponent = ({ data }) => {
  
  //dropDown button
  const [toggleClick, setToggleClick] = useState(false)

  ///////////Function handle event
   const handleToggleHover = ()=> {
    setToggleClick(true)
  }
  const handleToggleLeave = ()=> {
      setToggleClick(false)
  }
  
  const classNameChangeColor = toggleClick ? "hoverImageCard" : "dispayNone"

  return (
        <Card className="childElementCard">
            <CardActions onMouseEnter={handleToggleHover}
                        onMouseLeave={handleToggleLeave}>
                {/* <Link href="#"variant="body1"  underline="none"> */}
                <Box className={classNameChangeColor}>
                        <Button className="hoverButton">
                            Order now
                        </Button>
                    <CardContent>
                          <Typography gutterBottom variant="h6" component="div">
                        {data.foodName}
                          </Typography>
                    </CardContent>
                    </Box>
                    <CardMedia
                        component="img"
                        alt="This is product"
                        className="imageCard"
                        image={data.images[0].imageUrl }
                    />
                {/* </Link> */}
            </CardActions>
        </Card>
  )
};

export default CardComponent
